using P = Pair<ComparableList<Pair<long, long>>, long>;
public class HatParade
{
    public int getPermutation(int[] value, int[] sum)
    {
        var n = value.Length;
        var a = new long[n];
        var b = new long[n];
        for (int i = 0; i < n; i++)
        {
            a[i] = value[i];
            b[i] = sum[i];
        }
        return get(a, b);
    }
    public int get(long[] v, long[] t)
    {
        var n = v.Length;
        var s = v.Sum();
        var dp = new HashMap<P, ModInteger>();
        Func<ComparableList<Pair<long, long>>, long, long, ModInteger> dfs = null;
        dfs = (mask, ls, rs) =>
          {
              if (mask.Count == 0)
                  return 1;
              if (ls > rs) Swap(ref ls, ref rs);
              Debug.WriteLine(mask.AsJoinedString());
              Debug.WriteLine("{0} {1}", ls, rs);
              Debug.WriteLine("");
              ModInteger ret = 0;
              if (dp.TryGetValue(new P(mask, ls), out ret)) return ret;
              for (int i = 0; i < mask.Count; i++)
              {
                  if (ls + mask[i].x == mask[i].y || mask[i].y == s - ls)
                  {
                      var nm = new ComparableList<Pair<long, long>>();
                      for (int j = 0; j < mask.Count; j++)
                          if (j != i) nm.Add(mask[j]);
                      ret += dfs(nm, ls + mask[i].x, rs);
                  }
              }

              return dp[new P(mask, ls)] = ret;
          };
        var go = new ComparableList<Pair<long, long>>();
        for (int i = 0; i < n; i++)
            go.Add(new Pair<long, long>(v[i], t[i]));
        go.Sort();
        var ans = dfs(go, 0, 0);
        return (int)ans.num;
    }
    static public T[] Enumerate<T>(int n, Func<int, T> f) { var a = new T[n]; for (int i = 0; i < n; ++i) a[i] = f(i); return a; }
    static public void Swap<T>(ref T a, ref T b) { var tmp = a; a = b; b = tmp; }
}
